package com.yan.analysispageinfoimport org.junit.Test/** * Example local unit test, which will execute on the development machine (host). * * See [testing documentation](http://d.android.com/tools/testing). */class ExampleUnitTest {    @Test    fun addition_isCorrect() {        println("maxSubArray " + maxSubArray(intArrayOf(5, 4, -1, 7, 8)))    }    fun maxSubArray(nums: IntArray): Int {        if (nums.isEmpty()) return 0        var lastSum = nums[0]        var maxSum = lastSum        val count = nums.size        for (i in 1 until count) {            val cur = nums[i]            if (lastSum > 0) {                lastSum += cur            } else {                lastSum = cur            }            maxSum = Math.max(maxSum, lastSum)        }        return maxSum    }}